sys_script_fix.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
  // than the current time.  Only return NeedIt Task records that do not have
  // a State field value of Closed Complete.
var dueNITasks = new GlideRecord('x_58872_needit_needit_task');
dueNITasks.addQuery('due_date','>=', rightNow);
dueNITasks.addQuery('due_date','<', gs.hoursAgo(-24));
dueNITasks.addQuery('state', '!=', 3);

dueNITasks.query();

while (dueNITasks.next()) {
	gs.info("NeedIT tasks due in the next 24hours = " + dueNITasks.number);
}
