sys_script_include.script:
function validateEmailAddress(emailStr){
	//Use Javascript coercion to guarantee emailStr is a strng
	emailStr = emailStr + '';
	//Copmpare emailStr against the alloed syntax as specified in the REGEX
	if (emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else {
		return false;
	}
}
